SELECT MSG_ID,MSG_TITLE,MSG_TYPE_ID,CONTENT,CREATE_DATE,SENDOR,STATUS,IS_RECERVOR FROM MSG_BASE WHERE 1=1
AND MSG_TYPE_ID = ${MSG_TYPE_ID}
AND MSG_ID = ${MSG_ID}
AND MSG_TITLE LIKE '%${MSG_TITLE}%'
AND SENDOR = ${SENDOR}
ORDER BY CREATE_DATE DESC
LIMIT ${start},${rows};

SELECT COUNT(1) FROM MSG_BASE WHERE 1=1
AND MSG_TYPE_ID = ${MSG_TYPE_ID}
AND MSG_ID = ${MSG_ID}
AND MSG_TITLE LIKE '%${MSG_TITLE}%'
AND SENDOR = ${SENDOR}
